import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../redux/actions'; // Ensure this action is correct

const Cart = () => {
  // Accessing cart from the Redux state
  const cart = useSelector((state) => state.cart.items); // Assuming state.cart.items holds the cart items
  const dispatch = useDispatch();

  // Handle remove item action
  const handleRemove = (id) => {
    dispatch(removeFromCart(id)); // Dispatch action to remove the item from the cart
  };

  return (
    <div>
      <h2>Shopping Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div>
          <ul>
            {cart.map((item) => (
              <li key={item.id}>
                <div>
                  <h4>{item.name}</h4>
                  <p>Price: ${item.price}</p>
                  <button onClick={() => handleRemove(item.id)}>Remove</button>
                </div>
              </li>
            ))}
          </ul>
          <div>
            <h3>
              Total: $
              {cart.reduce((total, item) => total + item.price, 0)}
            </h3>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
