import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../redux/actions'; // Действие для удаления товара

const Cart = () => {
  const cart = useSelector(state => state.cart); // Получаем данные корзины из store
  const dispatch = useDispatch();

  const handleRemove = (product) => {
    dispatch(removeFromCart(product)); // Удаляем товар из корзины
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              <span>{item.name}</span> 
              <button onClick={() => handleRemove(item)}>Remove</button> {/* Кнопка удаления */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Cart;
