export const addToCart = (product) => ({
  type: 'ADD_TO_CART',
  payload: product, // Товар, который добавляется в корзину
});

export const removeFromCart = (product) => ({
  type: 'REMOVE_FROM_CART',
  payload: product, // Товар, который удаляется из корзины
});
