const initialState = {
  cart: [], // Корзина пуста по умолчанию
};

const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      return {
        ...state,
        cart: [...state.cart, action.payload], // Добавляем товар в корзину
      };
    case 'REMOVE_FROM_CART':
      return {
        ...state,
        cart: state.cart.filter(item => item.id !== action.payload.id), // Удаляем товар по id
      };
    default:
      return state;
  }
};

export default cartReducer;
