// redux/reducers.js

const initialState = {
  cart: [], // корзина пустая изначально
};

const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      return {
        ...state,
        cart: [...state.cart, action.payload], // добавляем новый товар в корзину
      };
    case 'REMOVE_FROM_CART':
      return {
        ...state,
        cart: state.cart.filter(item => item.id !== action.payload.id), // удаляем товар по id
      };
    case 'CLEAR_CART':
      return {
        ...state,
        cart: [], // очищаем корзину
      };
    default:
      return state;
  }
};

export default cartReducer;
