// src/components/HomePage.js
import React from 'react';
import ProductCard from './ProductCard';

const HomePage = ({ products, onAddToCart }) => {
    return (
        <div style={styles.pageContainer}>
            <h2 style={styles.heading}>Почему наша компания лучше</h2>
            <p style={styles.text}>
                Мы гордимся тем, что предлагаем нашим клиентам только качественную и проверенную бытовую технику. Наша компания отличается высоким уровнем обслуживания и вниманием к деталям. Мы предлагаем:
            </p>
            <ul style={styles.list}>
                <li>Широкий ассортимент товаров от ведущих мировых производителей.</li>
                <li>Гарантия на все товары, включая доставку и установку.</li>
                <li>Быструю доставку и удобные способы оплаты.</li>
                <li>Профессиональные консультации и помощь в выборе товаров.</li>
                <li>Постоянные акции и скидки для наших клиентов.</li>
            </ul>
            <h3 style={styles.productHeading}>Наши товары</h3>
            <div style={styles.productList}>
                {products.map((product) => (
                    <ProductCard key={product.id} product={product} onAddToCart={onAddToCart} />
                ))}
            </div>
        </div>
    );
};

const styles = {
    pageContainer: {
        padding: '20px',
        maxWidth: '1200px',
        margin: '0 auto',
    },
    heading: {
        fontSize: '32px',
        fontWeight: 'bold',
        marginBottom: '20px',
    },
    text: {
        fontSize: '18px',
        marginBottom: '20px',
        lineHeight: '1.6',
    },
    list: {
        fontSize: '18px',
        marginLeft: '20px',
        lineHeight: '1.6',
    },
    productHeading: {
        fontSize: '24px',
        fontWeight: 'bold',
        marginTop: '40px',
        marginBottom: '20px',
    },
    productList: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
    },
};

export default HomePage;
