import React from "react";
import ProductCard from "./components/ProductCard";

const HomePage = ({ products, addToCart }) => {
    return (
        <div style={styles.container}>
            <h2 style={styles.title}>Почему мы лучшие</h2>
            <div style={styles.whyUs}>
                <p style={styles.text}>
                    Мы - лидер на рынке бытовой техники, потому что предлагаем не только широкий ассортимент товаров, но и высокий уровень сервиса. Наша компания предлагает конкурентоспособные цены, быструю доставку и гарантию на все товары. Мы постоянно следим за новыми трендами в сфере технологий и стремимся предоставлять нашим клиентам только самые лучшие и современные продукты.
                </p>
                <ul style={styles.list}>
                    <li>• Широкий ассортимент</li>
                    <li>• Отличные цены</li>
                    <li>• Быстрая доставка</li>
                    <li>• Профессиональная поддержка</li>
                    <li>• Гарантия качества</li>
                </ul>
            </div>
            <h2 style={styles.title}>Наши товары</h2>
            <div style={styles.productGrid}>
                {products.map((product) => (
                    <ProductCard key={product.id} product={product} onAddToCart={addToCart} />
                ))}
            </div>
        </div>
    );
};

const styles = {
    container: {
        padding: "20px",
    },
    title: {
        textAlign: "center",
        fontSize: "28px",
        fontWeight: "bold",
        marginBottom: "20px",
    },
    whyUs: {
        marginBottom: "40px",
        backgroundColor: "#f9f9f9",
        padding: "20px",
        borderRadius: "10px",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    },
    text: {
        fontSize: "18px",
        lineHeight: "1.6",
        marginBottom: "20px",
    },
    list: {
        listStyleType: "none",
        padding: "0",
        fontSize: "16px",
        lineHeight: "1.8",
    },
    productGrid: {
        display: "grid",
        gridTemplateColumns: "repeat(3, 1fr)", // Три карточки в ряду
        gap: "20px", // Расстояние между карточками
        justifyContent: "center",
    },
};

export default HomePage;
