// src/components/Products.js

import React from 'react';
import { useDispatch } from 'react-redux';
import { addToCart } from '../redux/cartActions'; // Импортируем экшен

const Products = () => {
  const dispatch = useDispatch();

  // Функция для добавления товара в корзину
  const handleAddToCart = (product) => {
    dispatch(addToCart(product)); // Диспатчим экшен с продуктом
  };

  const products = [
    { id: 1, name: 'Washing Machine', price: 599.99 },
    { id: 2, name: 'Refrigerator', price: 899.99 },
    { id: 3, name: 'Microwave Oven', price: 199.99 },
  ];

  return (
    <div>
      {products.map((product) => (
        <div key={product.id}>
          <h3>{product.name}</h3>
          <p>${product.price}</p>
          <button onClick={() => handleAddToCart(product)}>Add to Cart</button>
        </div>
      ))}
    </div>
  );
};

export default Products;
