// redux/actions.js

export const addToCart = (product) => {
  console.log('Adding product to cart:', product);  // Для отладки
  return {
    type: 'ADD_TO_CART',
    payload: product,
  };
};

export const removeFromCart = (id) => {
  console.log('Removing product with id:', id);  // Для отладки
  return {
    type: 'REMOVE_FROM_CART',
    payload: { id },
  };
};
