// redux/reducers.js

const initialState = {
  cart: [], // Корзина пуста
};

const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      return {
        ...state,
        cart: [...state.cart, action.payload], // Добавляем товар в корзину
      };
    case 'REMOVE_FROM_CART':
      console.log('Removing product:', action.payload); // Логирование, чтобы убедиться, что действие работает
      return {
        ...state,
        cart: state.cart.filter(item => item.id !== action.payload.id), // Удаляем товар по id
      };
    case 'CLEAR_CART':
      return {
        ...state,
        cart: [], // Очищаем корзину
      };
    default:
      return state;
  }
};

export default cartReducer;
