import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ProductCard from './components/ProductCard';
import CartPage from './components/CartPage';

// Navbar Component
const Navbar = () => {
    return (
        <nav>
            <ul>
                <li><Link to="/">Home</Link></li>
                <li><Link to="/cart">Cart</Link></li>
            </ul>
        </nav>
    );
};

// Footer Component
const Footer = () => {
    return (
        <footer style={{ backgroundColor: '#333', color: 'white', textAlign: 'center', padding: '20px 0', marginTop: 'auto' }}>
            <p>&copy; 2025 My Store. All rights reserved.</p>
        </footer>
    );
};

const App = () => {
    const [cart, setCart] = useState([]);

    const products = [
        { id: 1, name: 'Product 1', price: 100, image: 'https://via.placeholder.com/150' },
        { id: 2, name: 'Product 2', price: 200, image: 'https://via.placeholder.com/150' },
        { id: 3, name: 'Product 3', price: 300, image: 'https://via.placeholder.com/150' },
    ];

    const addToCart = (product) => {
        setCart((prevCart) => {
            const existingProduct = prevCart.find(item => item.id === product.id);
            if (existingProduct) {
                return prevCart.map(item =>
                    item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
                );
            } else {
                return [...prevCart, { ...product, quantity: 1 }];
            }
        });
    };

    const removeFromCart = (productId) => {
        setCart((prevCart) => prevCart.filter(item => item.id !== productId));
    };

    return (
        <Router>
            <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
                <Navbar />
                <Routes>
                    <Route
                        path="/"
                        element={
                            <div className="product-list" style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', padding: '20px' }}>
                                {products.map(product => (
                                    <ProductCard key={product.id} product={product} onAddToCart={addToCart} />
                                ))}
                            </div>
                        }
                    />
                    <Route
                        path="/cart"
                        element={<CartPage cart={cart} onRemoveFromCart={removeFromCart} />}
                    />
                </Routes>
                <Footer />
            </div>
        </Router>
    );
};

export default App;
