import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import ProductCard from "./components/ProductCard";
import CartPage from "./components/CartPage";
import AboutUs from "./components/AboutUs";
import Contact from "./components/Contact";
import Footer from "./components/Footer";
import "./App.css";

const App = () => {
    const [cart, setCart] = useState([]);

    const products = [
        { id: 1, name: "Product 1", price: 100, image: "https://via.placeholder.com/300" },
        { id: 2, name: "Product 2", price: 200, image: "https://via.placeholder.com/300" },
        { id: 3, name: "Product 3", price: 300, image: "https://via.placeholder.com/300" },
        { id: 4, name: "Product 4", price: 150, image: "https://via.placeholder.com/300" },
        { id: 5, name: "Product 5", price: 250, image: "https://via.placeholder.com/300" },
        { id: 6, name: "Product 6", price: 350, image: "https://via.placeholder.com/300" },
    ];

    const addToCart = (product) => {
        setCart((prevCart) => {
            const existingProduct = prevCart.find((item) => item.id === product.id);
            if (existingProduct) {
                return prevCart.map((item) =>
                    item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
                );
            } else {
                return [...prevCart, { ...product, quantity: 1 }];
            }
        });
    };

    const removeFromCart = (productId) => {
        setCart((prevCart) =>
            prevCart
                .map((item) =>
                    item.id === productId ? { ...item, quantity: item.quantity - 1 } : item
                )
                .filter((item) => item.quantity > 0)
        );
    };

    const styles = {
        catalogContainer: {
            textAlign: "center",
            padding: "20px",
        },
        catalogTitle: {
            fontSize: "2rem",
            marginBottom: "20px",
            color: "#333",
        },
        productList: {
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))", // Автоматическая сетка с минимальной шириной 250px
            gap: "20px",
            justifyContent: "center",
            padding: "20px",
        },
        cartContainer: {
            position: "absolute",
            top: "20px",
            right: "20px",
            backgroundColor: "#fff",
            padding: "15px",
            borderRadius: "10px",
            boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
            zIndex: 10,
            transition: "transform 0.3s ease",
        },
        cartContainerMobile: {
            position: "static",
            marginTop: "20px",
            padding: "20px",
            boxShadow: "none",
        },
    };

    return (
        <Router>
            <Navbar />
            <Routes>
                <Route
                    path="/"
                    element={
                        <div style={styles.catalogContainer}>
                            <h1 style={styles.catalogTitle}>Каталог</h1>
                            <div className="product-list" style={styles.productList}>
                                {products.map((product) => (
                                    <ProductCard
                                        key={product.id}
                                        product={product}
                                        onAddToCart={addToCart}
                                    />
                                ))}
                            </div>
                            {/* Корзина для больших экранов */}
                            <div
                                style={{
                                    ...styles.cartContainer,
                                    ...window.innerWidth <= 768 ? styles.cartContainerMobile : {},
                                }}
                            >
                                <CartPage cart={cart} onRemoveFromCart={removeFromCart} />
                            </div>
                        </div>
                    }
                />
                <Route path="/about" element={<AboutUs />} />
                <Route path="/contact" element={<Contact />} />
                <Route
                    path="/cart"
                    element={<CartPage cart={cart} onRemoveFromCart={removeFromCart} />}
                />
            </Routes>
            <Footer />
        </Router>
    );
};

export default App;
