import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';

const Navbar = () => {
  const cart = useSelector(state => state.cart); // Получаем корзину из Redux store
  const cartCount = cart.length; // Считаем количество товаров в корзине

  return (
    <nav>
      <ul>
        <li><Link to="/">Home</Link></li>
        <li><Link to="/cart">Cart ({cartCount})</Link></li> {/* Ссылка на корзину с количеством товаров */}
      </ul>
    </nav>
  );
};

export default Navbar;
