import React from 'react';
import { useDispatch } from 'react-redux';
import { addToCart } from '../redux/cartActions'; // Импорт действия для добавления товара в корзину

const Products = () => {
  const dispatch = useDispatch();

  // Массив товаров
  const products = [
    {
      id: 1,
      name: 'Washing Machine',
      description: 'High-efficiency washing machine with quick wash options.',
      price: 599.99,
      image: 'https://via.placeholder.com/300x200',
    },
    {
      id: 2,
      name: 'Refrigerator',
      description: 'Smart refrigerator with energy-saving features.',
      price: 899.99,
      image: 'https://via.placeholder.com/300x200',
    },
    {
      id: 3,
      name: 'Microwave Oven',
      description: 'Compact and powerful microwave for your kitchen.',
      price: 199.99,
      image: 'https://via.placeholder.com/300x200',
    },
  ];

  // Обработчик для добавления товара в корзину
  const handleAddToCart = (product) => {
    dispatch(addToCart(product)); // Отправка действия для добавления товара в корзину
  };

  return (
    <section id="products" className="products">
      <h2>Our Top Products</h2>
      <div className="product-cards">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p className="price">${product.price}</p>
            <button className="btn-product" onClick={() => handleAddToCart(product)}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Products;
