import React from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; // Подключаем внешний CSS файл

const Navbar = () => {
    return (
        <nav className="navbar">
            <div className="navLinks">
                <Link to="/" className="navLink">Главная</Link>
                <Link to="/about" className="navLink">О нас</Link>
                <Link to="/contact" className="navLink">Контакты</Link>
                <Link to="/cart" className="cartLink">Корзина</Link>
            </div>
        </nav>
    );
};




const styles = {
    navbar: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '20px',
        backgroundColor: '#333',
        color: '#fff',
    },
    navLinks: {
        display: 'flex',
        gap: '30px',
        justifyContent: 'center',
        flexGrow: 1, // Чтобы ссылки оставались по центру
    },
    navLink: {
        textDecoration: 'none',
        color: '#fff',
        fontSize: '18px',
        fontWeight: 'bold',
        padding: '10px 20px',
        transition: 'background-color 0.3s',
    },
    cartLink: {
        textDecoration: 'none',
        color: '#fff',
        fontSize: '18px',
        fontWeight: 'bold',
        padding: '10px 20px',
        backgroundColor: '#007bff',
        borderRadius: '5px',
        transition: 'background-color 0.3s',
    },

    
};

export default Navbar;
