import React from "react";
import "./AboutUs.css"; // Обновите путь к CSS, если нужно

const AboutUs = () => {
    return (
        <div className="about-section section">
            <div className="content">
                <h2>О нас</h2>
                <p>
                    Мы — лидеры в продаже бытовой техники. Мы предлагаем широкий ассортимент товаров, высококачественные бренды и отличный сервис. Наша компания всегда стремится быть на шаг впереди, предоставляя самые современные решения для дома.
                </p>
                <ul>
                    <li>Широкий выбор товаров</li>
                    <li>Конкурентные цены</li>
                    <li>Быстрая доставка</li>
                    <li>Гарантия качества</li>
                </ul>
                <img src="https://via.placeholder.com/600x400" alt="Магазин" />
            </div>
        </div>
    );
};








const styles = {
    container: {
        padding: '20px',
        maxWidth: '1200px',
        margin: '0 auto',
    },
    title: {
        fontSize: '32px',
        fontWeight: 'bold',
        marginBottom: '20px',
    },
    text: {
        fontSize: '18px',
        lineHeight: '1.6',
        marginBottom: '15px',
    },
};

export default AboutUs;
