// src/redux/reducers/cartReducer.js
const initialState = {
  items: [],  // Начальное состояние корзины — пустой массив
  total: 0,   // Начальная общая сумма
};

const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      return {
        ...state,
        items: [...state.items, action.payload],  // Добавление товара
        total: state.total + action.payload.price,  // Обновление суммы
      };
    case 'REMOVE_FROM_CART':
      const updatedItems = state.items.filter(item => item.id !== action.payload);
      const removedItem = state.items.find(item => item.id === action.payload);
      return {
        ...state,
        items: updatedItems,
        total: state.total - (removedItem ? removedItem.price : 0),  // Корректировка суммы
      };
    default:
      return state;
  }
};

export default cartReducer;
