import React, { useState } from 'react';
import ProductCard from './components/ProductCard';
import Cart from './components/Cart';
import './app.css';

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const products = [
    {
      id: 1,
      name: 'Washing Machine',
      description: 'High-efficiency washing machine with quick wash options.',
      price: 599.99,
      image: 'https://via.placeholder.com/300x200',
    },
    {
      id: 2,
      name: 'Refrigerator',
      description: 'Smart refrigerator with energy-saving features.',
      price: 899.99,
      image: 'https://via.placeholder.com/300x200',
    },
    {
      id: 3,
      name: 'Microwave Oven',
      description: 'Compact and powerful microwave for your kitchen.',
      price: 199.99,
      image: 'https://via.placeholder.com/300x200',
    },
  ];

  const addToCart = (product) => {
    setCartItems([...cartItems, product]);
  };

  const removeFromCart = (product) => {
    setCartItems(cartItems.filter((item) => item.id !== product.id));
  };

  return (
    <div>
      <header>
        <nav>
          <ul className="nav-links">
            <li><a href="#">Home</a></li>
            <li><a href="#products">Products</a></li>
            <li><a href="#about">About Us</a></li>
            <li><a href="#contact">Contact Us</a></li>
            <li><a href="#cart" className="cart-link">Cart ({cartItems.length})</a></li>
          </ul>
        </nav>
      </header>

      <main>
        <section id="products" className="products">
          <h2>Our Top Products</h2>
          <div className="product-cards">
            {products.map((product) => (
              <ProductCard key={product.id} product={product} onAddToCart={addToCart} />
            ))}
          </div>
        </section>

        <section id="cart" className="cart-section">
          <Cart cartItems={cartItems} onRemoveFromCart={removeFromCart} />
        </section>
      </main>

      <footer>
        <p>&copy; 2025 Household Appliances. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default App;
