import React from 'react';
import { useDispatch } from 'react-redux';
import { addToCart } from '../redux/cartActions'; // Ensure you have addToCart action defined
import { products } from '../data'; // Import products from data.js

const Products = () => {
  const dispatch = useDispatch();

  // Handle Add to Cart action
  const handleAddToCart = (product) => {
    dispatch(addToCart(product)); // Dispatch product to the cart
  };

  return (
    <section id="products" className="products">
      <h2>Our Top Products</h2>
      <div className="product-cards">
        {products.map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p className="price">${product.price}</p>
            <button className="btn-product" onClick={() => handleAddToCart(product)}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Products;
