// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import ProductCard from './components/ProductCard'; // Путь к компоненту ProductCard
import CartPage from './components/CartPage'; // Новый компонент для корзины
import Navbar from './components/Navbar';
import { addToCart, removeFromCart } from './redux/actions'; // Правильный импорт из папки redux

const products = [
  { id: 1, name: 'Washing Machine', price: 599.99, description: 'High-efficiency washing machine', image: 'https://via.placeholder.com/300x200' },
  { id: 2, name: 'Refrigerator', price: 899.99, description: 'Smart refrigerator with energy-saving features', image: 'https://via.placeholder.com/300x200' },
  { id: 3, name: 'Microwave Oven', price: 199.99, description: 'Compact and powerful microwave', image: 'https://via.placeholder.com/300x200' },
];

const App = () => {
  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<ProductList />} />
        <Route path="/cart" element={<CartPage />} />
      </Routes>
    </Router>
  );
};

// Компонент с продуктами
const ProductList = () => (
  <div className="product-list">
    {products.map(product => (
      <ProductCard key={product.id} product={product} />
    ))}
  </div>
);

export default App;
