// src/components/CartPage.js
import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../кувгч/cartActions'; // Путь к экшенам

const CartPage = () => {
  const cartItems = useSelector(state => state.cart?.items); // Обработка undefined с использованием Optional Chaining
  const total = useSelector(state => state.cart?.total); // Обработка undefined с использованием Optional Chaining
  const dispatch = useDispatch();

  if (!cartItems || cartItems.length === 0) { // Проверяем, что cartItems не undefined и имеет элементы
    return <div>Корзина пуста</div>;
  }

  const handleRemoveFromCart = (id) => {
    dispatch(removeFromCart(id)); // Удаление товара из корзины
  };

  return (
    <div>
      <h1>Корзина</h1>
      <ul>
        {cartItems.map(item => (
          <li key={item.id}>
            {item.name} - {item.price} ₽
            <button onClick={() => handleRemoveFromCart(item.id)}>Удалить</button>
          </li>
        ))}
      </ul>
      <div>Итоговая сумма: {total} ₽</div>
    </div>
  );
};

export default CartPage;
