const initialState = {
    items: [],
    total: 0,
  };
  
  const cartReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'ADD_TO_CART':
        return {
          ...state,
          items: [...state.items, action.payload], // Добавление товара в корзину
          total: state.total + action.payload.price, // Обновление общей суммы
        };
      case 'REMOVE_FROM_CART':
        const itemToRemove = state.items.find(item => item.id === action.payload);
        return {
          ...state,
          items: state.items.filter(item => item.id !== action.payload), // Удаление товара из корзины
          total: state.total - (itemToRemove ? itemToRemove.price : 0), // Корректировка общей суммы
        };
      default:
        return state;
    }
  };
  
  export default cartReducer;
