// src/components/Cart.js

import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../redux/cartActions';

const Cart = () => {
  const cart = useSelector((state) => state.cart.items); // Получаем список товаров из состояния
  const dispatch = useDispatch();

  const handleRemove = (id) => {
    dispatch(removeFromCart(id)); // Удаляем товар из корзины по id
  };

  return (
    <div>
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              <h4>{item.name}</h4>
              <p>${item.price}</p>
              <button onClick={() => handleRemove(item.id)}>Remove</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Cart;
