export const addToCart = (product) => {
    return {
      type: 'ADD_TO_CART',
      payload: product,
    };
  };
  
  export const fetchProducts = () => {
    return async (dispatch) => {
      const response = await axios.get('https://api.example.com/products');
      dispatch({
        type: 'SET_PRODUCTS',
        payload: response.data,
      });
    };
  };
