// actions.js

export const addToCart = (product) => ({
  type: 'ADD_TO_CART',
  payload: product,
});

export const removeFromCart = (productId) => ({
  type: 'REMOVE_FROM_CART',
  payload: productId,
});

export const decreaseQuantity = (productId) => ({
  type: 'DECREASE_QUANTITY',
  payload: productId,
});
