import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import ProductCard from './components/ProductCard';
import CartPage from './components/CartPage';
import BestFeatures from './components/BestFeatures';
import Map from './components/Map';
import Contact from './components/Contact';
import Footer from './components/Footer';

const App = () => {
    const [cart, setCart] = useState([]);

    const products = [
        { id: 1, name: 'Product 1', price: 100, image: 'https://via.placeholder.com/150' },
        { id: 2, name: 'Product 2', price: 200, image: 'https://via.placeholder.com/150' },
        { id: 3, name: 'Product 3', price: 300, image: 'https://via.placeholder.com/150' },
        { id: 4, name: 'Product 4', price: 150, image: 'https://via.placeholder.com/150' },
        { id: 5, name: 'Product 5', price: 250, image: 'https://via.placeholder.com/150' },
        { id: 6, name: 'Product 6', price: 350, image: 'https://via.placeholder.com/150' },
    ];

    const addToCart = (product) => {
        setCart((prevCart) => {
            const existingProduct = prevCart.find(item => item.id === product.id);
            if (existingProduct) {
                return prevCart.map(item =>
                    item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
                );
            } else {
                return [...prevCart, { ...product, quantity: 1 }];
            }
        });
    };

    const removeFromCart = (productId) => {
        setCart((prevCart) => prevCart.filter(item => item.id !== productId));
    };

    return (
        <Router>
            <Navbar />
            <Routes>
                <Route
                    path="/"
                    element={
                        <div>
                            <BestFeatures />
                            <div className="product-list" style={styles.productList}>
                                {products.map(product => (
                                    <ProductCard key={product.id} product={product} onAddToCart={addToCart} />
                                ))}
                            </div>
                            <Map />
                            <Contact />
                        </div>
                    }
                />
                <Route
                    path="/cart"
                    element={<CartPage cart={cart} onRemoveFromCart={removeFromCart} />}
                />
            </Routes>
            <Footer />
        </Router>
    );
};

const styles = {
    productList: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        gap: '20px',
        padding: '20px',
    },
};

export default App;
