import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import ProductCard from './components/ProductCard';
import CartPage from './components/CartPage';

const App = () => {
    const [cart, setCart] = useState([]);

    const addToCart = (product) => {
        // Проверим, если товар уже есть в корзине, увеличим количество
        setCart(prevCart => {
            const existingProductIndex = prevCart.findIndex(item => item.id === product.id);
            if (existingProductIndex !== -1) {
                const updatedCart = [...prevCart];
                updatedCart[existingProductIndex].quantity += 1;
                return updatedCart;
            } else {
                return [...prevCart, { ...product, quantity: 1 }];
            }
        });
    };

    const products = [
        { id: 1, name: 'Product 1', price: 100, image: 'https://via.placeholder.com/150' },
        { id: 2, name: 'Product 2', price: 200, image: 'https://via.placeholder.com/150' },
        { id: 3, name: 'Product 3', price: 300, image: 'https://via.placeholder.com/150' },
    ];

    return (
        <Router>
            <Navbar />
            <Routes>
                <Route
                    path="/"
                    element={
                        <div className="product-list">
                            {products.map((product) => (
                                <ProductCard
                                    key={product.id}
                                    product={product}
                                    onAddToCart={addToCart} // Передаем функцию добавления товара
                                />
                            ))}
                        </div>
                    }
                />
                <Route
                    path="/cart"
                    element={<CartPage cart={cart} />}
                />
            </Routes>
        </Router>
    );
};

export default App;
