import { createStore, applyMiddleware } from 'redux';
import rootReducer from './reducers'; // Путь к вашему комбинированному редьюсеру
import { thunk } from 'redux-thunk'; // Исправленный импорт: используйте именованный экспорт

const store = createStore(
  rootReducer,
  window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__(), // Для использования Redux DevTools
  applyMiddleware(thunk) // Применяем middleware для работы с асинхронными действиями
);

export default store;
