import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { removeFromCart } from '../redux/cartActions'; // Импорт действия для удаления из корзины

const Cart = () => {
  const cart = useSelector((state) => state.cart.items); // Доступ к товарам в корзине из состояния Redux
  const total = useSelector((state) => state.cart.total); // Доступ к общей сумме
  const dispatch = useDispatch();

  const handleRemove = (id) => {
    dispatch(removeFromCart(id)); // Действие для удаления товара из корзины
  };

  return (
    <div>
      <h2>Shopping Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              <div>
                <h4>{item.name}</h4>
                <p>Price: ${item.price}</p>
                <button onClick={() => handleRemove(item.id)}>Remove</button>
              </div>
            </li>
          ))}
        </ul>
      )}
      <div className="total">
        <p>Total: ${total}</p>
      </div>
    </div>
  );
};

export default Cart;
