// src/redux/cartReducer.js

const initialState = {
    items: [],
    total: 0,
  };
  
  const cartReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'ADD_TO_CART':
        return {
          ...state,
          items: [...state.items, action.payload], // Add product to cart
          total: state.total + action.payload.price, // Update the total
        };
      case 'REMOVE_FROM_CART':
        return {
          ...state,
          items: state.items.filter(item => item.id !== action.payload), // Remove product from cart by id
          total: state.total - state.items.find(item => item.id === action.payload).price, // Adjust total
        };
      default:
        return state;
    }
  };
  
  export default cartReducer;
